CREATE TABLE `friendship` (
  `user_id1` int NOT NULL,
  `user_id2` int NOT NULL,
  `creation_time` timestamp DEFAULT CURRENT_TIMESTAMP,
  `update_time` timestamp DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `status` int DEFAULT 0 COMMENT '0 - pending; 1 - accepted',
  FOREIGN KEY (`user_id1`) REFERENCES `user`(`id`),
  FOREIGN KEY (`user_id2`) REFERENCES `user`(`id`),
  PRIMARY KEY (`user_id1`, `user_id2`),
  CHECK (`user_id1` != `user_id2`)
);

CREATE TABLE `notice` (
  `id` int NOT NULL AUTO_INCREMENT,
  `from_id` int DEFAULT NULL,
  `topic` varchar(10) DEFAULT NULL COMMENT 'like;comment;follow;',
  `to_id` int DEFAULT NULL,
  `entity_type` int DEFAULT NULL,
  `entity_id` int DEFAULT NULL,
  `status` int DEFAULT NULL COMMENT '0-unread;1-read;2-deleted;',
  `create_time` timestamp NULL DEFAULT current_timestamp,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`from_id`) REFERENCES user(id),
  FOREIGN KEY (`to_id`) REFERENCES user(id),
  KEY `index_from_id` (`from_id`),
  KEY `index_to_id` (`to_id`)
);


CREATE TABLE `discuss_post` (
                                `id` int NOT NULL AUTO_INCREMENT,
                                `user_id` int,
                                `title` varchar(100) DEFAULT NULL,
                                `content` text,
                                `type` int DEFAULT NULL COMMENT '0-regular; 1-pinned;',
                                `status` int DEFAULT NULL COMMENT '0-regular; 1-important; 2-banned;',
                                `create_time` timestamp NULL DEFAULT NULL,
                                `comment_count` int DEFAULT NULL,
                                `score` double DEFAULT NULL,
                                PRIMARY KEY (`id`),
                                FOREIGN KEY (`user_id`) REFERENCES user(`id`)
);

